mapper.select:
select * from assessment


        and name like '%${name}%'


        and state=#{state}

mapper.insert:
insert into assessment ( name, remarks, 
  state, beginDate, endDate, createDate
  )
values ( #{name,jdbcType=VARCHAR}, #{remarks,jdbcType=VARCHAR}, 
  #{state,jdbcType=CHAR}, #{beginDate,jdbcType=DATE}, #{endDate,jdbcType=DATE}, #{createDate,jdbcType=TIMESTAMP}
  )

mapper.update:
update assessment
set name = #{name,jdbcType=VARCHAR},
  remarks = #{remarks,jdbcType=VARCHAR}
where id = #{id,jdbcType=INTEGER}
